function Photo()
{
    filter_no = 100;
}
function downLoad()
{
    if (filter_no == 100)
    {
        save("Thanks for using photo frame app.jpeg");
        filter_no = 0;
    }
    else
    {
        window.alert("Take Photo First");
    }
}
function change_filter()
{
    filter_no = filter_no + 1;
    if (filter_no > 5)
    {
        filter_no = 1;
    }
}
function bear(x, y, size)
{
    strokeWeight(size / 20);
    fill("brown");
    circle(x - size/3, y - size/2, size/2.5);
    circle(x + size/3, y - size/2, size/2.5);
    fill("#E8BEAC");
    circle(x - size/3, y - size/2, (size/2.5) / 1.5);
    circle(x + size/3, y - size/2, (size/2.5) / 1.5);
    fill("brown");
    circle(x, y, size);
    fill("#E8BEAC");
    circle(x, y, size / 1.5);
    fill("black");
    stroke("black");
    strokeWeight(size / 20);
    circle(x, y, size / 15);
    line(x, y, x, y + size / 5);
    line(x - size / 15, y + size / 5, x + size / 15, y + size / 5 );
    circle(x - size / 7, y - size / 9, size / 30);
    circle(x + size / 7, y - size / 9, size / 30);
}
function fish(x, y, size, color)
{
    fill(color);
    strokeWeight(size / 120);
    stroke("black");
    triangle(x - size /10, y, x - size /1.5, y - size / 5, x - size / 1.5, y + size / 5);
    ellipse(x, y, size, size / 3);
    fill(255, 255, 255, 170);
    ellipse(x, y, size, size / 3);
    fill("black");
    stroke("black");
    idk = size / 150;
    strokeWeight(idk);
    circle(x + size / 3, y, size / 25);
    line(x + size / 10, y - size / 6, x + size / 10, y + size / 6);
    bubble(x + size / 1.5, y - size / 9 - sample_variable, sample_variable2);
    sample_variable = sample_variable + 0.1;
    if(sample_variable2 < 30)
    {
        sample_variable2 = sample_variable2 + 0.05;
    }

    if (y - size / 9 - sample_variable < -420)
    {
        sample_variable = 0;
        sample_variable2 = 0;
    }
}
function bubble(x, y, size)
{
    noStroke();
    fill(94, 168, 255, 200);
    circle(x, y, size);
    fill(255, 255, 255);
    circle(x + size / 3, y - size / 10, size / 7);
    noFill();
    strokeWeight(10);
}